import React, { useState } from 'react';
import NavBar from '../components/NavBar';
import PostingArea from '../components/PostingArea';
import UserPosts from '../components/UserPosts';
import GroupSidebar from '../components/GroupSidebar';
import FriendSidebar from '../components/FriendSideBar';

const UserPage = () => {
  const initialPosts = [
    {
      id: 1,
      content: "Just had an amazing coffee at my favorite cafÃ©!",
      author: "John Doe",
      avatar: "/images/avatar1.jpg",
      timestamp: "2024-08-18 09:00",
      imageStatus: "/images/avatar1.jpg",
      totalLike: 3,
      totalcComment: 3,
    },
    {
      id: 2,
      content: "Finished a great book today - highly recommend it!",
      author: "Jane Smith",
      avatar: "/images/avatar1.jpg",
      timestamp: "2024-08-18 11:30",
      imageStatus: "/images/avatar2.jpg",
      totalLike: 1,
      totalcComment: 3,
    },
    {
      id: 3,
      content: "Went for a long walk and enjoyed the sunset ðŸŒ…. Excited to start a new project at work tomorrow.",
      author: "Alice Johnson",
      avatar: '/images/avatar1.jpg',
      timestamp: "2024-08-17 19:15",
      imageStatus: "/images/avatar1.jpg",
      totalLike: 0,
      totalcComment: 0,
    },
    {
      id: 4,
      content: "Excited to start a new project at work tomorrow.",
      author: "Bob Brown",
      avatar: "/images/avatar2.jpg",
      timestamp: "2024-08-16 08:45",
      imageStatus: "/images/avatar1.jpg",
      totalLike: 2,
      totalcComment: 1,
    },
  ];

  // Function to add new posts
  const [posts, setPosts] = useState(initialPosts);

  // Function to add a new post
  const DisplayPost = (content) => {
    const newPost = {
      id: posts.length + 1, // Auto-incrementing ID
      content: content,
      author: "Current User", // Replace with actual user info
      timestamp: new Date().toLocaleString(),
    };
    setPosts([newPost, ...posts]);
  };

  return (
    <div>
      <NavBar />
      <div className="grid grid-cols-12 gap-4 p-4">
      <div className="col-span-3">
          <GroupSidebar />
        </div>

        <div className="col-span-6">
          <div className="mb-4">
            <h1 className="text-3xl font-bold">User Profile</h1>
          </div>

          <PostingArea addPost={DisplayPost} />
          <UserPosts posts={posts} />
        </div>

        <div className="col-span-3">
          <FriendSidebar />
        </div>
      </div>
    </div>
  );
};

export default UserPage;
