import React from 'react';
import './Post.css'; // Import your CSS file

const PostUser = ({ user }) => {
  return (
    <div className="post-user">
      <img src={user.profilePic} alt="User profile" />
      <span>{user.name}</span>
    </div>
  );
};

const PostStatus = ({ status }) => {
  return <div className="post-status">{status}</div>;
};

const PostImage = ({ image }) => {
  return <img src={image} alt="Post image" className="post-image" />;
};

const PostActions = ({ likes, comments, shares }) => {
  return (
    <div className="post-actions">
      <button>Like ({likes})</button>
      <button>Comment ({comments})</button>
      <button>Share ({shares})</button>
    </div>
  );
};

const PostComponent = ({ post }) => {
  return (
    <div className="post">
      <PostUser user={post.user} />
      <PostStatus status={post.status} />
      {post.image && <PostImage image={post.image} />}
      <PostActions likes={post.likes} comments={post.comments} shares={post.shares} />
    </div>
  );
};

export default PostComponent;
