import React from 'react';

const UserPosts = ({ initialPosts  }) => {

  const usePosts = (initialPosts) => {
    const [posts, setPosts] = useState(initialPosts);

    const addPost = (content) => {
      const newPost = {
        id: posts.length + 1,
        content: content,
        author: "Current User", // Replace with actual user info
        timestamp: new Date().toLocaleString(),
      };
      setPosts([newPost, ...posts]);
    };

    return {
      posts,
      addPost,
    };
  };

  const { posts } = usePosts(initialPosts);

  return (
    <div className="mt-4">
      {posts.length > 0 ? (
        posts.map((post) => (
          <div key={post.id} className="mb-4 p-4 border rounded shadow-sm bg-white">
            <p className="text-lg">{post.content}</p>
            <div className="text-sm text-gray-500">
              <p>Posted by {post.author}</p>
              <p>{post.timestamp}</p>
            </div>
          </div>
        ))
      ) : (
        <p>No posts yet. Be the first to post something!</p>
      )}
    </div>
  );
};

export default UserPosts;
