import React, { useState } from 'react';
import NavBar from '../components/NavBar';
import PostingArea from '../components/PosttingArea';
import UserPosts from '../components/UserPosts';

const UserPage = () => {
  // Example posts data
  const initialPosts = [
    "Just had an amazing coffee at my favorite cafÃ©!",
    "Finished a great book today - highly recommend it!",
    "Went for a long walk and enjoyed the sunset ðŸŒ…",
    "Excited to start a new project at work tomorrow.",
  ];

  // Set up state with initial posts
  const [posts, setPosts] = useState(initialPosts);

  // Function to add new posts
  const addPost = (newPost) => {
    setPosts([newPost, ...posts]);
  };

  return (
    <div>
      <NavBar />
      <div className="grid grid-cols-12 gap-4 p-4">
        <div className="col-span-9">
          <div className="mb-4">
            <h1 className="text-3xl font-bold">User Profile</h1>
            {/* Additional user info can go here */}
          </div>
          <PostingArea addPost={addPost} />
          <UserPosts posts={posts} />
        </div>
        <div className="col-span-3">
          {/* You can add user-related components like friends or groups here */}
        </div>
      </div>
    </div>
  );
};

export default UserPage;
