import React, { useState } from 'react';

const PostingArea = ({ onImageUpload }) => {
  const [post, setPost] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      if (onImageUpload) {
        onImageUpload(file); // Call the parent component's handler with the selected file
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Posting: ${post}`);
    setPost('');
  };

  return (
    <div className="p-4 border">
      <form onSubmit={handleSubmit}>
        <textarea
          className="w-full p-2 border border-gray-300 rounded"
          placeholder="What's on your mind?"
          value={post}
          onChange={(e) => setPost(e.target.value)}
        ></textarea>
        <div className='flex justify-between'>
        <div>
      <label htmlFor="image-upload" className="flex items-center py-2 px-4 border rounded text-gray-500 border-gray-300 cursor-pointer">
        <img 
          src="/path/to/add-image-icon.png" // Update this path to the location of your add image icon
          alt="Add Image Icon"
          className="w-5 h-5 mr-2"
        />
        Add Image
      </label>
      <input
        id="image-upload"
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        style={{ display: 'none' }} // Hide the default file input
      />
      {selectedImage && (
        <div className="mt-2">
          <img 
            src={selectedImage} 
            alt="Selected Preview"
            className="w-20 h-20 object-cover border rounded"
          />
        </div>
      )}
    </div>
          <button type="submit" className="mt-2 bg-blue-500 text-white px-4 py-2 rounded">
            Post
          </button>
        </div>
      </form>
    </div>
  );
};

export default PostingArea;
