import React from 'react';

const UserPosts = ({ posts }) => {
  return (
    <div className="mt-4">
      {posts.length > 0 ? (
        posts.map((post) => (
          <div key={post.id} className="mb-4 p-4 border rounded shadow-sm bg-white">
            <div className='flex items-center'>
              <img 
                src={post.avatar} 
                alt={`Avartar description`} 
                className="w-12 h-12 rounded-full mr-4"
              />
              <div>
                <p>{post.author}</p>
                
                <div className="text-sm text-gray-500">
                  <p>{post.timestamp}</p>
                </div>
              </div>
            </div>
            <p className="text pt-2">{post.content}</p>
            {/* <img 
                src={post.avatar} 
                alt={`Image description`} 
                className=""
              /> */}
              
          </div>
        ))
      ) : (
        <p className='text-center'>No posts yet. Be the first to post something!</p>
      )}
    </div>
  );
};

export default UserPosts;
